// src/components/Tasks.jsx
import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  fetchTasks,
  addTaskAsync,
  updateTaskAsync,
  deleteTaskAsync,
} from '../redux/slices/taskSlice';

const Tasks = () => {
  const dispatch = useDispatch();
  const { tasks, loading, error } = useSelector((state) => state.tasks);
  const { token, user } = useSelector((state) => state.auth); // Include user info

  const [newTaskData, setNewTaskData] = useState({
    title: '',
    description: '',
    category_id: '',
    priority: '',
    estimate: '',
    due_date: '',
  });

  useEffect(() => {
    if (token) {
      dispatch(fetchTasks());
      console.log("User Info:", user);
      console.log("Token:", token);
    }
  }, [dispatch, token, user]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTaskData((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddTask = async () => {
    if (!newTaskData.title.trim()) return;
    const taskPayload = {
      ...newTaskData,
      category_id: newTaskData.category_id ? parseInt(newTaskData.category_id) : null,
      estimate: newTaskData.estimate || '',
      due_date: newTaskData.due_date || null,
      completed: false,
    };
    console.log('Dispatching addTaskAsync with:', taskPayload);
    try {
      await dispatch(addTaskAsync(taskPayload)).unwrap();
      console.log('Task dispatched successfully');
      setNewTaskData({
        title: '',
        description: '',
        category_id: '',
        priority: '',
        estimate: '',
        due_date: '',
      });
    } catch (err) {
      console.error('Failed to add task:', err);
    }
  };

  const handleToggleComplete = (task) => {
    dispatch(updateTaskAsync({ ...task, completed: !task.completed }));
  };

  const handleDeleteTask = (id) => {
    dispatch(deleteTaskAsync(id));
  };

  return (
    <div>
      <h2>Tasks</h2>
      {user && <p>Welcome, {user.username} ({user.email})</p>}

      {loading && <p>Loading tasks...</p>}
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}

      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {tasks.length === 0 && !loading && <li>No tasks found.</li>}
        {tasks.map((task) => (
          <li
            key={task.id}
            style={{ marginBottom: '0.5rem', display: 'flex', alignItems: 'center' }}
          >
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => handleToggleComplete(task)}
              style={{ marginRight: '8px' }}
            />
            <span style={{ flexGrow: 1, textDecoration: task.completed ? 'line-through' : 'none' }}>
              {task.title} - {task.priority} - {task.due_date}
            </span>
            <button
              onClick={() => handleDeleteTask(task.id)}
              style={{
                backgroundColor: 'transparent',
                border: 'none',
                color: 'red',
                cursor: 'pointer',
                fontWeight: 'bold',
                marginLeft: '8px',
              }}
              aria-label={`Delete task ${task.title}`}
            >
              &times;
            </button>
          </li>
        ))}
      </ul>

      <div style={{ marginTop: '1rem' }}>
        <h3>Add New Task</h3>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={newTaskData.title}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newTaskData.description}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <input
          type="number"
          name="category_id"
          placeholder="Category ID"
          value={newTaskData.category_id}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <input
          type="text"
          name="priority"
          placeholder="Priority (e.g. High, Medium, Low)"
          value={newTaskData.priority}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <input
          type="text"
          name="estimate"
          placeholder="Estimate (e.g. 3h, 2d)"
          value={newTaskData.estimate}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <input
          type="date"
          name="due_date"
          value={newTaskData.due_date}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <button onClick={handleAddTask} style={{ marginTop: '8px', padding: '6px 12px' }}>
          Add Task
        </button>
      </div>
    </div>
  );
};

export default Tasks;
