import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  fetchTasks,
  addTaskAsync,
  updateTaskAsync,
  deleteTaskAsync,
} from '../redux/slices/taskSlice';
import { fetchCategories } from '../redux/slices/categorySlice';

const Tasks = () => {
  const dispatch = useDispatch();
  const { tasks, loading, error } = useSelector((state) => state.tasks);
  const { token, user } = useSelector((state) => state.auth);

  const categoryState = useSelector((state) => state.categories) || {};
  const { categories = [], loading: catLoading, error: catError } = categoryState;

  const [newTaskTitle, setNewTaskTitle] = useState('');
  const [newTaskDescription, setNewTaskDescription] = useState('');
  const [newTaskDueDate, setNewTaskDueDate] = useState('');
  const [newTaskCategory, setNewTaskCategory] = useState('');

  useEffect(() => {
    if (token) {
      dispatch(fetchTasks());
      dispatch(fetchCategories());
    }
  }, [dispatch, token]);

  const handleAddTask = async () => {
    if (!newTaskTitle.trim()) return;
    const taskData = {
      title: newTaskTitle,
      description: newTaskDescription,
      due_date: newTaskDueDate,
      category: newTaskCategory,
      completed: false,
    };
    try {
      await dispatch(addTaskAsync(taskData)).unwrap();
      setNewTaskTitle('');
      setNewTaskDescription('');
      setNewTaskDueDate('');
      setNewTaskCategory('');
    } catch (err) {
      console.error('Failed to add task:', err);
    }
  };

  const handleToggleComplete = (task) => {
    dispatch(updateTaskAsync({ ...task, completed: !task.completed }));
  };

  const handleDeleteTask = (id) => {
    dispatch(deleteTaskAsync(id));
  };

  const handleEditTask = (task) => {
    const newTitle = prompt('Edit title', task.title);
    const newDescription = prompt('Edit description', task.description);
    const newDueDate = prompt('Edit due date (YYYY-MM-DD)', task.due_date);
    const newCategory = prompt('Edit category', task.category);
    if (newTitle !== null) {
      dispatch(updateTaskAsync({
        ...task,
        title: newTitle,
        description: newDescription,
        due_date: newDueDate,
        category: newCategory,
      }));
    }
  };

  return (
    <div>
      <h2>Tasks</h2>
      {user && <p>Welcome, {user.username} ({user.email})</p>}

      {loading && <p>Loading tasks...</p>}
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}

      {catLoading && <p>Loading categories...</p>}
      {catError && <p style={{ color: 'red' }}>Category Error: {catError}</p>}

      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {tasks.length === 0 && !loading && <li>No tasks found.</li>}
        {tasks.map((task) => (
          <li
            key={task.id}
            style={{ marginBottom: '0.5rem', display: 'flex', alignItems: 'center' }}
          >
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => handleToggleComplete(task)}
              style={{ marginRight: '8px' }}
            />
            <span style={{ flexGrow: 1, textDecoration: task.completed ? 'line-through' : 'none' }}>
              {task.title} | {task.description} | Due: {task.due_date} | Category: {task.category}
            </span>
            <button onClick={() => handleEditTask(task)} style={{ marginLeft: '8px' }}>Edit</button>
            <button
              onClick={() => handleDeleteTask(task.id)}
              style={{
                backgroundColor: 'transparent',
                border: 'none',
                color: 'red',
                cursor: 'pointer',
                fontWeight: 'bold',
                marginLeft: '8px',
              }}
              aria-label={`Delete task ${task.title}`}
            >
              &times;
            </button>
          </li>
        ))}
      </ul>

      <div style={{ marginTop: '1rem' }}>
        <input
          type="text"
          placeholder="New task title"
          value={newTaskTitle}
          onChange={(e) => setNewTaskTitle(e.target.value)}
          style={{ padding: '6px', width: '250px' }}
        />
        <input
          type="text"
          placeholder="Description"
          value={newTaskDescription}
          onChange={(e) => setNewTaskDescription(e.target.value)}
          style={{ padding: '6px', width: '250px', marginLeft: '8px' }}
        />
        <input
          type="date"
          value={newTaskDueDate}
          onChange={(e) => setNewTaskDueDate(e.target.value)}
          style={{ padding: '6px', marginLeft: '8px' }}
        />
        <select
          value={newTaskCategory}
          onChange={(e) => setNewTaskCategory(e.target.value)}
          style={{ padding: '6px', marginLeft: '8px' }}
        >
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat.id} value={cat.name}>{cat.name}</option>
          ))}
        </select>
        <button onClick={handleAddTask} style={{ marginLeft: '8px', padding: '6px 12px' }}>
          Add Task
        </button>
      </div>
    </div>
  );
};

export default Tasks;
