// src/components/Tasks.jsx
import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  fetchTasks,
  addTaskAsync,
  updateTaskAsync,
  deleteTaskAsync,
} from '../redux/slices/taskSlice';

const Tasks = () => {
  const dispatch = useDispatch();
  const { tasks, loading, error } = useSelector((state) => state.tasks);
  const { token, user } = useSelector((state) => state.auth);

  const [newTaskData, setNewTaskData] = useState({
    title: '',
    description: '',
    category_id: '',
    priority: '',
    estimate: '',
    due_date: '',
  });

  const [editTaskId, setEditTaskId] = useState(null);
  const [editTaskData, setEditTaskData] = useState({});

  // Mock categories (replace with real categories from Redux or fetch API)
  const categories = [
    { id: 1, name: 'Work' },
    { id: 2, name: 'Personal' },
    { id: 3, name: 'Urgent' },
  ];

  useEffect(() => {
    if (token) {
      dispatch(fetchTasks());
    }
  }, [dispatch, token]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTaskData((prev) => ({ ...prev, [name]: value }));
  };

  const handleEditInputChange = (e) => {
    const { name, value } = e.target;
    setEditTaskData((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddTask = async () => {
    if (!newTaskData.title.trim()) return;
    const taskPayload = {
      ...newTaskData,
      category_id: newTaskData.category_id ? parseInt(newTaskData.category_id) : null,
      estimate: newTaskData.estimate || '',
      due_date: newTaskData.due_date || null,
      completed: false,
    };
    try {
      await dispatch(addTaskAsync(taskPayload)).unwrap();
      setNewTaskData({
        title: '',
        description: '',
        category_id: '',
        priority: '',
        estimate: '',
        due_date: '',
      });
    } catch (err) {
      console.error('Failed to add task:', err);
    }
  };

  const handleToggleComplete = (task) => {
    dispatch(updateTaskAsync({ ...task, completed: !task.completed }));
  };

  const handleDeleteTask = (id) => {
    dispatch(deleteTaskAsync(id));
  };

  const startEditing = (task) => {
    setEditTaskId(task.id);
    setEditTaskData({
      title: task.title,
      description: task.description,
      category_id: task.category_id,
      priority: task.priority,
      estimate: task.estimate,
      due_date: task.due_date ? task.due_date.split('T')[0] : '',
    });
  };

  const handleUpdateTask = async (id) => {
    const updatedTask = {
      id,
      ...editTaskData,
      category_id: editTaskData.category_id ? parseInt(editTaskData.category_id) : null,
      estimate: editTaskData.estimate || '',
      due_date: editTaskData.due_date || null,
    };
    try {
      await dispatch(updateTaskAsync(updatedTask)).unwrap();
      setEditTaskId(null);
    } catch (err) {
      console.error('Failed to update task:', err);
    }
  };

  const cancelEdit = () => {
    setEditTaskId(null);
    setEditTaskData({});
  };

  return (
    <div>
      <h2>Tasks</h2>
      {user && <p>Welcome, {user.username} ({user.email})</p>}

      {loading && <p>Loading tasks...</p>}
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}

      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {tasks.length === 0 && !loading && <li>No tasks found.</li>}
        {tasks.map((task) => (
          <li
            key={task.id}
            style={{ marginBottom: '0.5rem', display: 'flex', flexDirection: 'column', border: '1px solid #ccc', padding: '8px', borderRadius: '4px' }}
          >
            {editTaskId === task.id ? (
              <>
                <input
                  type="text"
                  name="title"
                  value={editTaskData.title}
                  onChange={handleEditInputChange}
                  placeholder="Title"
                />
                <input
                  type="text"
                  name="description"
                  value={editTaskData.description}
                  onChange={handleEditInputChange}
                  placeholder="Description"
                />
                <select
                  name="category_id"
                  value={editTaskData.category_id}
                  onChange={handleEditInputChange}
                >
                  <option value="">Select Category</option>
                  {categories.map((cat) => (
                    <option key={cat.id} value={cat.id}>{cat.name}</option>
                  ))}
                </select>
                <input
                  type="text"
                  name="priority"
                  value={editTaskData.priority}
                  onChange={handleEditInputChange}
                  placeholder="Priority"
                />
                <input
                  type="text"
                  name="estimate"
                  value={editTaskData.estimate}
                  onChange={handleEditInputChange}
                  placeholder="Estimate"
                />
                <input
                  type="date"
                  name="due_date"
                  value={editTaskData.due_date}
                  onChange={handleEditInputChange}
                />
                <button onClick={() => handleUpdateTask(task.id)}>Save</button>
                <button onClick={cancelEdit} style={{ marginLeft: '4px' }}>Cancel</button>
              </>
            ) : (
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => handleToggleComplete(task)}
                  style={{ marginRight: '8px' }}
                />
                <span style={{ flexGrow: 1, textDecoration: task.completed ? 'line-through' : 'none' }}>
                  {task.title} - {task.priority} - {task.due_date}
                </span>
                <button onClick={() => startEditing(task)} style={{ marginLeft: '4px' }}>Edit</button>
                <button
                  onClick={() => handleDeleteTask(task.id)}
                  style={{
                    backgroundColor: 'transparent',
                    border: 'none',
                    color: 'red',
                    cursor: 'pointer',
                    fontWeight: 'bold',
                    marginLeft: '4px',
                  }}
                  aria-label={`Delete task ${task.title}`}
                >
                  &times;
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>

      <div style={{ marginTop: '1rem' }}>
        <h3>Add New Task</h3>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={newTaskData.title}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newTaskData.description}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <select
          name="category_id"
          value={newTaskData.category_id}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        >
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat.id} value={cat.id}>{cat.name}</option>
          ))}
        </select><br />
        <input
          type="text"
          name="priority"
          placeholder="Priority"
          value={newTaskData.priority}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <input
          type="text"
          name="estimate"
          placeholder="Estimate"
          value={newTaskData.estimate}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <input
          type="date"
          name="due_date"
          value={newTaskData.due_date}
          onChange={handleInputChange}
          style={{ padding: '6px', width: '250px', marginBottom: '4px' }}
        /><br />
        <button onClick={handleAddTask} style={{ marginTop: '8px', padding: '6px 12px' }}>
          Add Task
        </button>
      </div>
    </div>
  );
};

export default Tasks;
