// src/components/Tasks.jsx
import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  fetchTasks,
  addTaskAsync,
  updateTaskAsync,
  deleteTaskAsync,
} from '../redux/slices/taskSlice';
import { fetchCategories } from '../redux/slices/categorySlice';

const Tasks = () => {
  const dispatch = useDispatch();

  const { tasks, loading: tasksLoading, error: tasksError } = useSelector((state) => state.tasks);
  const { categories, error: categoriesError, loading: categoriesLoading } = useSelector((state) => state.categories);
  const { token, user } = useSelector((state) => state.auth);

  const [newTaskTitle, setNewTaskTitle] = useState('');
  const [newTaskDescription, setNewTaskDescription] = useState('');
  const [newTaskPriority, setNewTaskPriority] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  const [editTaskId, setEditTaskId] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [editPriority, setEditPriority] = useState('');
  const [editCategory, setEditCategory] = useState('');

  useEffect(() => {
    if (token) {
      dispatch(fetchTasks());
      dispatch(fetchCategories());
    }
  }, [dispatch, token]);

  const handleAddTask = async () => {
    if (!newTaskTitle.trim()) return;
    if (!selectedCategory) {
      alert('Please select a category.');
      return;
    }

    const newTask = {
      title: newTaskTitle,
      description: newTaskDescription || '',
      priority: newTaskPriority || 'Normal',
      category_id: selectedCategory,
      completed: false,
    };

    try {
      await dispatch(addTaskAsync(newTask)).unwrap();
      setNewTaskTitle('');
      setNewTaskDescription('');
      setNewTaskPriority('');
      setSelectedCategory('');
    } catch (err) {
      console.error('Failed to add task:', err);
    }
  };

  const handleToggleComplete = (task) => {
    dispatch(updateTaskAsync({
      ...task,
      completed: !task.completed,
    }));
  };

  const handleDeleteTask = (id) => {
    dispatch(deleteTaskAsync(id));
  };

  const getCategoryName = (categoryId) => {
    const category = categories.find((cat) => cat.id === categoryId);
    return category ? category.name : 'Uncategorized';
  };

  const startEditing = (task) => {
    setEditTaskId(task.id);
    setEditTitle(task.title);
    setEditDescription(task.description);
    setEditPriority(task.priority);
    setEditCategory(task.category_id);
  };

  const cancelEditing = () => {
    setEditTaskId(null);
    setEditTitle('');
    setEditDescription('');
    setEditPriority('');
    setEditCategory('');
  };

  const handleUpdateTask = async () => {
    const updatedTask = {
      id: editTaskId,
      title: editTitle,
      description: editDescription,
      priority: editPriority,
      category_id: editCategory,
      completed: false,
    };

    try {
      await dispatch(updateTaskAsync(updatedTask)).unwrap();
      cancelEditing();
    } catch (err) {
      console.error('Failed to update task:', err);
    }
  };

  return (
    <div>
      <h2>Tasks</h2>
      {user && <p>Welcome, {user.username} ({user.email})</p>}

      {tasksLoading && <p>Loading tasks...</p>}
      {tasksError && <p style={{ color: 'red' }}>Error: {tasksError}</p>}
      {categoriesError && <p style={{ color: 'red' }}>Category Error: {categoriesError}</p>}

      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {tasks.length === 0 && !tasksLoading && <li>No tasks found.</li>}
        {tasks.map((task) => (
          <li key={task.id} style={{ marginBottom: '1rem' }}>
            {editTaskId === task.id ? (
              <>
                <input
                  type="text"
                  value={editTitle}
                  onChange={(e) => setEditTitle(e.target.value)}
                  placeholder="Title"
                  style={{ marginBottom: '4px', padding: '4px', width: '250px' }}
                /><br />
                <input
                  type="text"
                  value={editDescription}
                  onChange={(e) => setEditDescription(e.target.value)}
                  placeholder="Description"
                  style={{ marginBottom: '4px', padding: '4px', width: '250px' }}
                /><br />
                <select
                  value={editPriority}
                  onChange={(e) => setEditPriority(e.target.value)}
                  style={{ marginBottom: '4px', padding: '4px', width: '250px' }}
                >
                  <option value="Low">Low</option>
                  <option value="Normal">Normal</option>
                  <option value="High">High</option>
                </select><br />
                <select
                  value={editCategory}
                  onChange={(e) => setEditCategory(e.target.value)}
                  style={{ marginBottom: '4px', padding: '4px', width: '250px' }}
                >
                  {categories.map((cat) => (
                    <option key={cat.id} value={cat.id}>{cat.name}</option>
                  ))}
                </select><br />
                <button onClick={handleUpdateTask} style={{ padding: '4px 10px', marginRight: '6px' }}>Save</button>
                <button onClick={cancelEditing} style={{ padding: '4px 10px' }}>Cancel</button>
              </>
            ) : (
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => handleToggleComplete(task)}
                  style={{ marginRight: '8px' }}
                />
                <span style={{ flexGrow: 1, textDecoration: task.completed ? 'line-through' : 'none' }}>
                  {task.title} - {task.description} [{task.priority}] (Category: {getCategoryName(task.category_id)})
                </span>
                <button
                  onClick={() => startEditing(task)}
                  style={{
                    backgroundColor: 'orange',
                    color: 'white',
                    border: 'none',
                    cursor: 'pointer',
                    padding: '4px 8px',
                    marginLeft: '8px',
                  }}
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteTask(task.id)}
                  style={{
                    backgroundColor: 'transparent',
                    border: 'none',
                    color: 'red',
                    cursor: 'pointer',
                    fontWeight: 'bold',
                    marginLeft: '8px',
                  }}
                  aria-label={`Delete task ${task.title}`}
                >
                  &times;
                </button>
              </div>
            )}
            {Array.isArray(task.suggestions) && task.suggestions.length > 0 && (
            <div className="suggestions" style={{ marginTop: '4px', paddingLeft: '24px' }}>
              <h4>Suggestions:</h4>
              <ul>
                {task.suggestions.map((suggestion, idx) => (
                  <li key={idx}>{suggestion}</li>
                ))}
              </ul>
            </div>
          )}
          </li>
        ))}
      </ul>

      <div style={{ marginTop: '1rem' }}>
        <input
          type="text"
          placeholder="New task title"
          value={newTaskTitle}
          onChange={(e) => setNewTaskTitle(e.target.value)}
          style={{ padding: '6px', width: '250px', marginBottom: '6px' }}
        /><br />
        <input
          type="text"
          placeholder="Task description (optional)"
          value={newTaskDescription}
          onChange={(e) => setNewTaskDescription(e.target.value)}
          style={{ padding: '6px', width: '250px', marginBottom: '6px' }}
        /><br />
        <select
          value={newTaskPriority}
          onChange={(e) => setNewTaskPriority(e.target.value)}
          style={{ padding: '6px', width: '250px', marginBottom: '6px' }}
        >
          <option value="">Select Priority</option>
          <option value="Low">Low</option>
          <option value="Normal">Normal</option>
          <option value="High">High</option>
        </select><br />
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          style={{ padding: '6px', width: '250px', marginBottom: '6px' }}
        >
          <option value="">Select Category</option>
          {categoriesLoading && <option disabled>Loading categories...</option>}
          {categoriesError && <option disabled>Error loading categories</option>}
          {Array.isArray(categories) && !categoriesLoading && !categoriesError
            ? categories.map((cat) => (
                <option key={cat.id} value={cat.id}>{cat.name}</option>
              ))
            : null
          }
        </select><br />
        <button onClick={handleAddTask} style={{ padding: '6px 12px' }}>
          Add Task
        </button>
      </div>
    </div>
  );
};

export default Tasks;
